mapper.insert:

	INSERT INTO franchisee_faq(
		faq_title,
		faq_content
	) VALUES (
		#{faqTitle},
		#{faqContent}
	)

mapper.select:
SELECT faq_no as faqNo, faq_title as faqTitle, faq_content as faqContent FROM franchisee_faq
faq_title like CONCAT('%', #{searchWord}, '%')
LIMIT #{beginRow}, #{rowPerPage}

mapper.update:

  UPDATE franchisee_qna SET 
     qna_answer = #{qnaAnswer}
  WHERE 
     qna_no = #{qnaNo}
