mapper.select:
SELECT file_name
FROM food_pic
WHERE food_no = #{foodNo}

mapper.insert:
INSERT INTO del_food_reservation ( food_no, reservation_count, reservation_date, customer_no, customer_name) SELECT
food_no, reservation_count, reservation_date, customer_no, customer_name FROM food_reservation
WHERE reservation_no = #{reservationNo}

mapper.update:

  UPDATE customer SET 
     customer_name = #{customerName},
     customer_email = #{customerEmail},
     customer_phone = #{customerPhone}
  WHERE 
     customer_no = #{customerNo}


	UPDATE franchisee_spec
	SET
		cpu = #{cpu},
		vga = #{vga},
		ram = #{ram}
	WHERE franchisee_no = #{franchiseeNo}

mapper.select:
SELECT faq_no as faqNo, faq_title as faqTitle, faq_content as faqContent FROM franchisee_faq
faq_title like CONCAT('%', #{searchWord}, '%')
LIMIT #{beginRow}, #{rowPerPage}
